function loadROM(files)
	{
	try
		{
		// GETTING THE FILE EXTENSION
		var extension = files[0].name.split(".").pop().toLowerCase();

		// CHECKING THE FILE EXTENSION
		if (extension=="nes")
			{
			var filereader = new FileReader();
			filereader.file_name = files[0].name;
			filereader.onload = function()
				{
				// CLEARING THE ROMDATA VARIABLE
				ROMDATA = "";

				// SETTING THE ROM FILENAME
				ROMNAME = files[0].name;

				// GETTING THE DATA FILE
				var RAWDATA = new Uint8Array(this.result);
				for (i = 0; i < RAWDATA.length; i++)
					{
					ROMDATA = ROMDATA + String.fromCharCode(RAWDATA[i]);
					}

				// CLEARING THE SELECTED FILE VALUE
				document.getElementsByClassName("gui_title")[0].style.display = "none";

				// STARTING/RESTARING THE ROM
				restartROM();

				// CLEARING THE SELECTED FILE VALUE
				document.getElementsByClassName("gui_file")[0].value = null;
				};

			// READING THE FILE
			filereader.readAsArrayBuffer(files[0]);
			}
			else
			{
			// CLEARING THE SELECTED FILE VALUE
			document.getElementsByClassName("gui_file")[0].value = null;

			// SHOWING AN ALERT MESSAGE WITH THE COMPATIBLE FILE FORMAT
			alertCustom(STRING_ERROREXTENSION_TITLE,STRING_ERROREXTENSION_MESSAGE,STRING_ERROREXTENSION_ACCEPT);
			}
		}
		catch(err)
		{
		}
	}

