function download_Blob(data, fileName, mimeType)
	{
	var blob, url;
	blob = new Blob([data], {type: mimeType});
	url = window.URL.createObjectURL(blob);
	download_URL(url, fileName);
	}

function download_URL(data, fileName)
	{
	var iOS = !!navigator.platform && /iPad|iPhone|iPod/.test(navigator.platform);

	if (iOS==true)
		{
		// IOS REQUIRES THAT THE BLOB URL MUST BE OPENED IN A NEW TAB
		// BECAUSE OTHERWISE THE CURRENT GAME/WINDOW/LOCATION WILL BE LOST.
		window.open(data, "_blank");
		}
		else
		{
		var a;
		a = document.createElement("a");
		a.href = data;
		a.download = fileName;
		document.body.appendChild(a);
		a.style = "display: none";
		a.click();
		a.remove();
		}
	}

function loadGameState(files)
	{
	try
		{
		// CHECKING THE FILE EXTENSION
		var extension = files[0].name.split(".").pop().toLowerCase();

		// CHECKING IF THE USER IS USING IOS. SAFARI DOESN'T WORK PROPERLY WITH BLOB ELEMENTS.
		// BECAUSE OF THAT, ALL THE DOWNLOADED STATE ARE GOING TO BE SOMETHING LIKE
		// UNKNOWN.DMS AND WON'T BE A .STATE FILE.
		var iOS = !!navigator.platform && /iPad|iPhone|iPod/.test(navigator.platform);

		if (extension=="state" || iOS==true)
			{
			// READING THE SELECTED FILE
			var filereader = new FileReader();
			filereader.file_name = files[0].name;
			filereader.onload = function()
				{
				// READING THE GAME STATE FROM THE FILE
				NintendoGameState = JSON.parse(this.result);

				// GETTING THE ORIGINAL SOUND CONFIG (ON/OFF)
				NintendoGameSoundInitialState = NintendoEmulator.opts.emulateSound;

				// MUTING THE SOUND EMULATION
				NintendoEmulator.opts.emulateSound = false;

				// CLEARING THE EMULATOR STATE
				NintendoEmulator.reset();

				// RESTARTING THE ROM
				restartROM();

				// WAITING FOR THE GAME TO BE LOADED
				setTimeout(function()
					{
					// LOADING THE GAME STATE
					NintendoEmulator.fromJSON(NintendoGameState);

					// RESUMING THE GAME
					NintendoEmulator.start();

					// RESTORING THE ORIGINAL SOUND CONFIG (ON/OFF)
					NintendoEmulator.opts.emulateSound = NintendoGameSoundInitialState;
					}, 300);

				// CLEARING THE SELECTED FILE VALUE
				document.getElementsByClassName("gui_file_state")[0].value = null;

				// HIDING THE 'FILE SAVED' LABEL (JUST IN CASE)
				document.getElementsByClassName("gui_saved")[0].style.display = "none";
				};
			filereader.readAsText(files[0]);
			}
		}
		catch(err)
		{
		}
	}

